%%%-------------------------------------------------------------------
%%% @author yujian
%%% @doc 屏蔽字符
%%% API:cpn_mask_word:check(<<"我么那都由一个家名字叫中国，wogan，wocao, 毛泽东"/utf8>>) -> <<"我么那都由一个家名字叫中国，wogan，wo***,******"/utf8>>.
%%%
%%% Created : 16. 五月 2016 下午6:49
%%%-------------------------------------------------------------------
-module(cpn_mask_word).

-export([check/1, checkRes/1, checkRes/2, test/0, get_mask_words/0]).

check(Str) ->
    [NewStr, _Bool] = checkRes(Str),
    NewStr.

%% IsBool::true=带敏感字，false=不带敏感字
%%-spec checkRes(Str :: binary()) -> [NewStr :: binary(), IsBool:: atom()].
checkRes(Str) ->
    checkRes(Str, get_mask_words()).

checkRes(Str, Rule) ->
    case re:run(Str, Rule, [global, notempty]) of
        nomatch ->
            [Str, false];
        {match, MatchList} ->
            FunFoldl =
                fun(Match, Acc) ->
                    ByteSize = byte_size(Acc),
                    case [{S, L} || {S, L} <- lists:usort(Match), L =/= 0] of
                        [] -> Acc;
                        [{0, Len} | _R] ->
                            Tail = binary:part(Acc, Len, ByteSize - Len),
                            <<(iolist_to_binary(["*" || _I <- lists:seq(1, Len)]))/binary, Tail/binary>>;
                        [{SIndex, Len} | _R] ->
                            Head = binary:part(Acc, 0, SIndex),
                            Tail = binary:part(Acc, SIndex + Len, ByteSize - SIndex - Len),
                            <<Head/binary, (iolist_to_binary(["*" || _I <- lists:seq(1, Len)]))/binary, Tail/binary>>
                    end
                end,
            [lists:foldl(FunFoldl, Str, MatchList), true]
    end.


test() ->
    T1 = erlang:monotonic_time(),
    AAA = test(1),
    T2 = erlang:monotonic_time(),
    Time = erlang:convert_time_unit(T2 - T1, native, micro_seconds),
    io:format("111:~p~n222:~p~n", [AAA, Time]).

test(1000) -> check(<<"*交,wogan,wocao, 彭丽媛，彭丽媛, 全能神教 "/utf8>>);
test(N) ->
    check(<<"我么那都由一个家名字叫中国，*交，wocao, 彭丽媛，彭丽媛, 全能神教 "/utf8>>),
    test(N + 1).

